import React, { useState, useContext, useEffect, useRef } from "react";
import "../../assets/styles/cabinet.css";
import Camera from "../../assets/images/camera.png";
import User from "../../assets/images/user.png";
const CreateCabinetScreen = () => {
  const userInfo = JSON.parse(localStorage.getItem("registrationData"));
  const [favoriteBooks, setFavoriteBooks] = useState(
    JSON.parse(localStorage.getItem("favoriteBooks"))
  );
  return (
    <div className="cabinet screen beet">
      <Account userInfo={userInfo} favoriteBooks={favoriteBooks} />
    </div>
  );
};
export default CreateCabinetScreen;
const Account = ({ userInfo, favoriteBooks }) => {
  const [photo, setPhoto] = useState(userInfo.photo ? userInfo.photo : User);
  const changeHandler = (src) => {
    setPhoto(src);
    localStorage.setItem(JSON.stringify({ ...userInfo, photo: src }));
  };
  const getAuthor = () => {};
  const getCategory = () => {};
  return (
    <div className="cabinet__contact beet2">
      <div className="cabinet__circle center">
        <img className="cabinet__photo" src={photo} />
        <div className="cabinet__makePhoto center">
          <input type="file" onChange={changeHandler} />
          <img src={Camera} />
        </div>
      </div>
      <p className="title cabinet__greenTitle margin">{userInfo.fullName}</p>
      <div className="cabinet__miniTable">
        <p className="margin">
          Favourite author : <span>{getAuthor()}</span>
        </p>
        <p className="margin">
          Favourite ganre : <span>{getCategory()}</span>
        </p>
        <p className="margin">
          Total books : <span>{favoriteBooks.length}</span>
        </p>
      </div>
    </div>
  );
};
