import React, { useState, useEffect, useRef, useMemo } from "react";
import Logo from "../assets/images/logo.png";
import YouTube from "../assets/images/youTube.png";
import Facebook from "../assets/images/facebook.png";
import Instagram from "../assets/images/instagram.png";
import {StyleRoot} from 'radium';
import animations from "../animations";
import { useNavigate } from "react-router-dom";
import User from "../assets/images/user.png"
const Header = () => {
  const [mobileMode, setMobileMode] = useState(false);
  const [isMobileMenuOpened,setIsMobileMenuOpened]=useState(false);
  const [activePage,setActivePage]=useState("/");
  const navigate = useNavigate();
  const resize = () => {
    const width = window.innerWidth;
    if (width > 900 && mobileMode) {
      setMobileMode(false);
      setIsMobileMenuOpened(false);
    }
    else if (width < 900 && !mobileMode) setMobileMode(true);
  };
  const linkClickHandler=data=>{
    if (activePage==data) return;
    setActivePage(data);
    navigate(data);
    console.log(window.location);
  }
  useEffect(() => {
    const onHashChange=()=>{

    }
    window.addEventListener("resize", resize);
    window.onhashchange=onHashChange;
    return () => window.removeEventListener("resize", resize);
  });
  useEffect(() => resize(), []);
  return (<>
    <div className="header beet">
      <img className="header__logo" src={Logo} onClick={()=>linkClickHandler("/")}/>
      {!mobileMode && <HeaderContent {...{linkClickHandler,activePage}}/>}
      {mobileMode && <Burger setIsMobileMenuOpened={setIsMobileMenuOpened}/>}
    </div>
    {isMobileMenuOpened && <StyleRoot style={animations.slideInUp} className="header__popup">
      <HeaderContent {...{linkClickHandler,activePage}}/>
    </StyleRoot>}
  </>);
};
export default Header;
const Burger = ({setIsMobileMenuOpened}) => (
  <div className="header__burger beet2" onClick={()=>setIsMobileMenuOpened(curr=>!curr)}>
    <p></p>
    <p></p>
    <p></p>
    <p></p>
  </div>
);
const HeaderContent = ({linkClickHandler,activePage}) => {
  const condition=JSON.parse(localStorage.getItem("registrationData")).isSignedIn;
  const getStyles=(data)=>{
    return (data==activePage) ? {opacity:0.5} : {};
  }
  const user=<img src={User} className="header__user" onClick={()=>linkClickHandler("/cabinet")} style={getStyles("/cabinet")}/>
  const element = condition ? user : <>
    <p onClick={()=>linkClickHandler("/signIn")} style={getStyles("/signIn")}>Sign In</p>
    <p onClick={()=>linkClickHandler("/signUp")} style={getStyles("/signUp")}>Sign up</p>
  </>;
  return (<>
  <div className="header__titles beet">
    <p onClick={()=>linkClickHandler("/passwordGenerator")} style={getStyles("/passwordGenerator")}>Password generator</p>
    <p onClick={()=>linkClickHandler("/library")} style={getStyles("/library")}>Library</p>
    {element}
  </div>
  <div className="header__media beet">
    <div className="header__ball center">
      <img src={YouTube} />
    </div>
    <div className="header__ball center">
      <img src={Facebook} className="header__imgHeight" />
    </div>
    <div className="header__ball center">
      <img src={Instagram} />
    </div>
  </div>
  </>)
};
