export default (book,options={type:"",value:""},filters,results=40)=>{
	const key = "AIzaSyBEIqK7j9rLo7FHM4Fdl41EITEsGLHFx9E",
	url="https://www.googleapis.com/books";
	const option=(options.type!="") ? `+${options.type}:${options.value}` : "";
	const filter=(filters!=undefined && filters!="") ? `&filter=${filters}` : ``;
  return fetch(`${url}/v1/volumes/?q=${book}${option}${filter}&key=${key}&maxResults=${results}`);
}
