import React,{useState,useContext,useEffect,createContext} from "react";
import "../../assets/styles/main.css";
import {SearchContext} from "../../App";
import OfferBooks from "./components/OfferBooks";
import Article from "./components/Article";
import SearchResults from "./components/SearchResults";
import fetcher from "../../functions/fetcher";
import compare from "../../functions/compare";
export const SortContext=createContext();
const CreateMainScreen = () => {
  const {searchActive}=useContext(SearchContext);
  const [foundItems,setFoundItems]=useState([]);
  const [bookView,setBookView]=useState("normal");
  const [nothingFound,setNothingFound]=useState(false);
  const [sortedItems,setSortedItems]=useState([]);
  const [loading,setLoading]=useState(false);
  const sortOptions={
    disabled:"disabled",
    ascending:"ascending",
    descending:"descending"
  }
  const [sorting,setSorting]=useState({
    money:sortOptions.disabled,
    rating:sortOptions.disabled,
    date:sortOptions.disabled
  });
  const sort=()=>{
    let entries=Object.entries(sorting);
    const sortedData=[...foundItems];
    const count=(type,obj)=>{
      if (type=="money") {
        if (obj.canBeBought) return +(obj.listPrice.split(" ")[0]);
        return 0;
      }
      if (type=="rating") return obj.rating;
    }
    const compareDate=(curr,next)=>{
      let currDate=curr.publishedDate;
      let nextDate=next.publishedDate;
      if (currDate=="Unknown") return 0;
      if (nextDate=="Unknown") return 1;
      currDate=currDate.split("-");
      nextDate=nextDate.split("-");
      //if (currDate.length==1) return
    }
    entries=entries.filter(elem=>elem[1]!=sortOptions.disabled)
    if (entries.length==0) return setSortedItems([]);

    if (entries.length==1) {
      const type=entries[0][0];
      const getCount=count.bind(null,type);
      sortedData.sort((curr,next)=>{
        if (entries[0][1]==sortOptions.ascending) return getCount(curr)-getCount(next);
        else return getCount(next)-getCount(curr);
      })
    }

    setSortedItems([...sortedData]);
  }

  return (
    <div className="main beet">
    <SortContext.Provider value={{sorting,setSorting,sortOptions,sort}}>
      <Article style="main__mainInput" {...{setFoundItems,foundItems,setBookView,bookView,nothingFound,setNothingFound,setLoading}}/>
    </SortContext.Provider>
      <div className="main__body">
			{searchActive ? <SearchResults {...{foundItems,bookView,nothingFound,sortedItems,loading}}/> : <OfferBooks/>}
			</div>
    </div>
  );
};
export default CreateMainScreen;
