import React,{useState,useContext,useEffect} from "react";
import "../../assets/styles/main.css";
import topics from "./topics";
import BookCard from "../../globalComponents/BookCard"
import {SearchContext} from "../../App";
import fetcher from "../../functions/fetcher";
import formater from "../../functions/formater"
const CreateMainScreen = () => {
	const {searchActive}=useContext(SearchContext);
  return (
    <div className="main beet">
			<Article/>
      <article className="main__article" ></article>
      <div className="main__body">
			{searchActive ? <SearchResults/> : <OfferBooks/>}
			</div>
    </div>
  );
};
export default CreateMainScreen;
const SearchResults=()=>null;
const Article=()=>null;
const OfferBooks=()=>{
	const [data,setData]=useState([]);
	const random=(a,b)=> {
		return Math.floor(a+Math.random()*(b+1-a));
	}
	useEffect(()=>{
		const requestNumber=6;
		const previousData=JSON.parse(localStorage.getItem("searchInfo"));
		const randomTopics=[...topics];
		randomTopics.sort(()=>0.5-Math.random());

		let mass=[{title:"Books from author you liked",key:"likedBookAuthors"},{title:"Books you were searching for",key:"prevBookTopics"},{title:"You may also like",key:"prevLikedBooks"}].forEach(async ({title,key})=>{
			if (previousData[key].length==0) return;
			requestNumber--;
			let requestData;
			if (random(0,1)==0) {
				previousData[key].sort(()=>0.5-Math.random());
				requestData=previousData[key][0];
			} else {
				requestData=previousData[key][previousData[key].length-1];
			}
			request(requestData,title)
		})

		for (let i=0;i<requestNumber;i++) request(randomTopics[i],randomTopics[i])
		async function request(mass,title) {
			try {
				let response=await fetcher(mass);
				let {items}=await response.json();
				items=items.map((elem)=>formater(elem));
				if (random(0,1)==0) {
					items.sort(()=>0.5-Math.random());
				} else {
					items.sort((next,curr)=>{

					})
				}
				setData((curr)=>[...curr,{title:title,elements:items.slice(0,6)}]);
			}
			catch (error) {
				console.log(error);
			}
		}
	},[]);
	return (
		data.map(({title,elements})=><>
			<p className="title">{title}</p>
			<p className="main__line"/>
			{elements.map((elem)=><BookCard data={elem} mode="normal"/>)}
		</>)
	);
};
