import React, { useState, useContext, useEffect, useMemo } from "react";
import ArrowLeft from "../assets/images/arrowLeft.png";
import ArrowRight from "../assets/images/arrowRight.png"
const PageSlider=({currentPage,setCurrentPage,pagesLength})=>{
	const blocks=useMemo(()=>{
		const mass=[];
		for (let i=0;i<pagesLength;i++) {
			mass.push(<Block {...{currentPage,setCurrentPage,i}}/>)
		}
		return mass;
	},[pagesLength,currentPage]);
	const arrowClickHandler=(type)=>{
		if (type=="left") {
			if (currentPage==0) return;
			setCurrentPage(curr=>curr-1);
		} else {
			if (currentPage==pagesLength-1) return;
			setCurrentPage(curr=>curr+1);
		}
	}
	return (pagesLength!=1 && <div className="pageSlider">
		<div className={currentPage==0 && "pageSlider-disabled"} onClick={()=>arrowClickHandler("left")}>
			<img src={ArrowLeft}/>
		</div>
		{blocks}
		<div>
			<img src={ArrowRight} className={(currentPage==pagesLength-1) && "pageSlider-disabled"} onClick={()=>arrowClickHandler("right")}/>
		</div>
	</div>)
}
export default PageSlider;
const Block=({currentPage,setCurrentPage,i})=>{
	const clickHandler=()=>{
		if (i==currentPage) return;
		setCurrentPage(i);
	}
	return <div onClick={clickHandler} className={currentPage==i && "pageSlider-active"}>
		<p>{i+1}</p>
	</div>
}
