import React, { useState, useContext, useEffect, useRef,useMemo, createContext } from "react";
import BookModeSwitcher from "../../../globalComponents/BookModeSwitcher";
import Input from "../../../globalComponents/Input";
import BookCard from "../../../globalComponents/BookCard";
import PageSlider from "../../../globalComponents/PageSlider";
const Favorites=({favoriteBooks})=>{
	const [bookMode,setBookMode]=useState("normal");
	const [filterText,setFilterText]=useState("");
	const [currentPage,setCurrentPage]=useState(0);
	const [filteredBooks,setFilteredBooks]=useState([]);
	const [isFilterActive,setIsFilterActive]=useState(false);
	const pages=useMemo(()=>{
		const books=(isFilterActive ? filteredBooks : favoriteBooks).map(elem=><BookCard data={elem} mode={bookMode}/>);
		const pages=[];
		const iterations=Math.ceil(books.length/12);
		for (let i=0;i<iterations;i++) {
			pages.push(books.slice(i*iterations,(i*12)+12))
		}
		return pages;
	},[bookMode,isFilterActive,currentPage,favoriteBooks.length]);
	useEffect(()=>{
		if (filterText=="") return setIsFilterActive(false);
		if (!isFilterActive) setIsFilterActive(true);
		let mass=isFilterActive.filter(({title})=>title.slice(0,filterText.length).toLo)
	},[filterText]);
	return (<div className="cabinet__liked">
		<div className="cabinet__header beet width">
			<p className="title">Your liked books</p>
			<div className="cabinet__aligner beet">
				<BookModeSwitcher data={bookMode} setData={setBookMode}/>
				<Input setData={setFilterText} placeholder="type name , category , author of the book" style="cabinet-left"/>
			</div>
		</div>
		<div className="cabinet__body width">
			<div className={bookMode=="normal" ? "grid" : "row"}>
					{pages[currentPage]}
			</div>
			{pages.length>1 && <PageSlider {...{currentPage,setCurrentPage}} pagesLength={pages.length}/>}
		</div>
	</div>);
}
export default Favorites;
