import React, { useState, useContext, useEffect, useMemo } from "react";
import NotFound from "../../../assets/images/notFound.png";
import BookCard from "../../../globalComponents/BookCard";
const SearchResults = ({ foundItems, bookView, nothingFound, sortedItems }) => {
  const books = useMemo(() => {
    return (sortedItems.length == 0 ? foundItems : sortedItems).map((elem) => (
      <BookCard data={elem} mode={bookView} />
    ));
  }, [foundItems.length, bookView]);
  return (
    <>

      {nothingFound ? (
        <div className="popup center">
          <div className="width beet2">
            <img src={NotFound} />
            <p>Unfortunatelly 0 books was found</p>
          </div>
        </div>
        ) : (<>
          <p className="title">Found {foundItems.length} results</p>
      <p className="main__line" />
        <div className={bookView == "normal" ? "grid" : "row"}>{books}</div>
            </>)}
    </>
  );
};
export default SearchResults;
