import React, {
  useState,
  useContext,
  useEffect,
  useRef,
  createContext,
  useMemo,
} from "react";
import EmptyLibrary from "../../assets/images/emptyLibrary.png";
import Input from "../../globalComponents/Input";
import Plus from "../../assets/images/plus.png";
import Delete from "../../assets/images/delete.png";
import "../../assets/styles/library.css";
import ArrowUpward from "../../assets/images/arrowUpward.png";
import ArrowDownward from "../../assets/images/arrowDownward.png";
const CreateLibraryScreen = () => {
  const [libraries, setLibraries] = useState(
    JSON.parse(localStorage.getItem("libraries")) || []
  );
  const [libraryName,setLibraryName]=useState("");
  const createLibraryHandler=()=>{
    if (libraryName=="") return;
    for (let i=0;i<libraries.length;i++) {
      if (libraries[i]==libraryName) return;
    }
    libraries.push({title:libraryName,books:[]});
    localStorage.setItem("libraries",JSON.stringify(libraries));
    setLibraries([...libraries]);
  }
  const displayLibraries=useMemo(()=>{
    return libraries.map((elem,ind)=><Library {...{setLibraries,ind}} title={elem.title} books={elem.books}/>)
  },[libraries.length]);
  return (
    <div className="library screen">
      <div className="library__form width">
        <div className="library__container">
          <Input
            setData={setLibraryName}
            placeholder="type the name of the library"
            style="library__inputMain"
          />
          <div className="button library__button blue left" onClick={createLibraryHandler}>
            <div className="beet">
              <img src={Plus} />
              <p className="left">Create library</p>
            </div>
          </div>
        </div>
      </div>
      {libraries.length == 0 ? (
        <div className="popup center library__popup">
          <div className="width beet2 stretch">
            <img src={EmptyLibrary} />
            <p>There is bookshelfs in your library , click to create one</p>
          </div>
        </div>
      ) : (
        <div className="library__row row">
          {displayLibraries}
        </div>
      )}
    </div>
  );
};
export default CreateLibraryScreen;
const Library = ({ title,books,setLibraries,ind }) => {
	const [showHiddenBooks,setShowHiddenBooks]=useState(false);
	const deleteHandler=()=>{
    if (window.confirm(`Are you sure you want to delete ${title} library`)) {
      setLibraries(curr=>curr.filter((_,i)=>i!=ind))
    }
  }
  return (
    <div className="library__line">
      <div className="beet">
        <p className="title">{title}</p>
        <div className="button black library__button addBooks">
          <div className="beet">
            <img src={Plus} />
            <p>Add books</p>
          </div>
        </div>
      </div>
      <div className="library__bookShelf grid"></div>
      <div className="library__buttons beet">
        <div className="button black library__button addBooks screen-top" onClick={deleteHandler}>
          <div className="beet">
            <img src={Delete} />
            <p>Delete library</p>
          </div>
        </div>
				<div className={"button library__button addBooks screen-top "+(showHiddenBooks ? "green" : "blue")} onClick={()=>setShowHiddenBooks(curr=>!curr)}>
          <div className="beet">
            <img src={showHiddenBooks ? ArrowUpward : ArrowDownward} />
            <p>Show {showHiddenBooks ? "less" : "more"}</p>
          </div>
        </div>
      </div>
    </div>
  );
};
