import React,{useEffect, useState} from "react";
import NotSelected from "../assets/images/favourite.png";
import Selected from "../assets/images/favouriteActive.png";
import compare from "../functions/compare";
const Book=({data,mode})=>{
	const [liked,setLiked]=useState(false);
	const returnBook=()=>{
		const image=<img src={data.image} className="book__image"/>,
		title=<p className="book__title">{data.title}</p>,
		stars=<div className="book__stars">{data.ratingStarsArray.map(elem=><img src={elem}/>)}</div>;
		if (mode=="normal") return (<div className="book beet2 book__align" style={{width:235}}>
			<div className="book__imageBlock">
				{image}
				<Hearth {...{liked,setLiked,data}}/>
			</div>
			{title}
			<Title data={data} show/>
			<div className="book__container beet">
				{stars}
				<p className="book__author">{data.authors}</p>
			</div>
			<Button data={data} style="book__normalButton"/>
		</div>);

		// else if (mode=="large") return <div className="book beet">
		// 	{image}
		// 	<div className="book__largeContainer"></div>
		// </div>;

		else return <div className="book">
			{image}
			{title}
		</div>;
	}
	return returnBook()
};
export default Book;
const Button=({data,style})=><a className={"button "+style} href={data.link} target="_blank">
			<p>{data.canBeBought ? `Buy` : "Read"} for <span>{data.canBeBought ? data.listPrice : "free"}</span></p>
</a>;
const Hearth=({liked,setLiked,data})=>{
	const clickHandler=()=>{
		let mass=JSON.parse(localStorage.getItem("favoriteBooks"));
		if (!liked) mass.push(data);
		else {
			let index;
			mass.forEach((elem,ind) => {
				if (compare(elem,data)) index=ind;
			});
			mass.splice(index,1);
		}
		setLiked(curr=>!curr);
		localStorage.setItem("favoriteBooks",JSON.stringify(mass));
	}
	return (<img className="book__hearth" src={liked ? Selected : NotSelected} onClick={clickHandler}/>)
}
const Title=({data:{title}})=>{
	return
}
