import React, { useState, useContext, useEffect, useRef, createContext } from "react";
import Container from "../../globalComponents/Container";
import Secure from "../../assets/images/secure.png";
import "../../assets/styles/passwordGenerator.css";
import CheckBox from "./components/CheckBox";
const CheckBoxesContext=createContext();
const PasswordGenerator=()=>{
	const copyHandler=()=>{}
	const generatePasswordHandler=()=>{}
	const [includingData,setIncludingData]=useState({
		lowerCase:true,
		upperCase:false,
		numbers:true,
		symbols:false
	})
	return (<Container style="generator">
		<img src={Secure} className="screen__image"/>
		<p className="screen-margin title generator__center">Generate a strong password with our generator</p>
		<div className="generator__grid screen-margin">
		<CheckBoxesContext.Provider value={[includingData,setIncludingData]}>

		</CheckBoxesContext.Provider>
			<div>
				<p className="blackTitle">Letters</p>
				<CheckBox text="include lowercase : (a,b,c,d,e,f)" type="lowerCase"/>
				<CheckBox text="include uppercase : (A,B,C,D,E,F)" type="upperCase"/>
			</div>
			<div>
				<p className="blackTitle">Numbers</p>

			</div>
		</div>
		<div className="button screen__button" onClick={generatePasswordHandler}>
			<p className="screen__bigTitle screen-margin">Create Password</p>
		</div>
		<div className="generator__menu beet">
			<p className="blackTitle">Your password :</p>
			<input type="text" className="generator__output"/>
			<div className="generator__copy button" onClick={copyHandler}>
				<p>Copy</p>
			</div>
		</div>
	</Container>);
}
export default PasswordGenerator;
