import Input from "../../../globalComponents/Input";
import Down from "../../../assets/images/down.png";
import ArrowLeft from "../../../assets/images/arrowLeft.png";
import ArrowRight from "../../../assets/images/arrowRight.png"
const BookDragSlider=({setAddingBook,favoriteBooks})=>{
	return (<div className="library__bookDrag">
		<div className="library__controls beet">
			<Input placeholder="type the name/author of the book"/>
			<div className="library__close center">
				<img src={Down}/>
			</div>
		</div>
		<div className="library__miniBooks beet">
			<div className="library__slider">
				<img src={ArrowLeft}/>
			</div>
			<div className="library__slider">
				<img src={ArrowLeft}/>
			</div>
		</div>
	</div>);
}
export default BookDragSlider;
