import React,{useState} from "react";
export default ({data,mode})=>{
	const [liked,setLiked]=useState(false);
	const returnBook=()=>{
		const image=<img src={data.image}/>,
		title=<p className="book__title">{data.title}</p>,
		stars=<div className="book__stars">{data.ratingStarsArray.map(elem=><img src={elem}/>)}</div>,
		button=<div className="button">
			<p>{data.canBeBought ? `Buy for <p></p>${data.listPrice}` : "Read for free"}</p>
		</div>;
		if (mode=="normal") return <div className="book beet2 book__align">
			{image}
			{title}
			<div className="beet">
				{stars}
				<p className="book__author">{data.authors}</p>
			</div>
			{button}
		</div>;
		else if (mode=="large") return <div className="book beet">
			{image}
			<div className="book__largeContainer"></div>
		</div>;
		else return <div className="book">
			{image}
			{title}
		</div>;
	}
	return returnBook()
};
