import React,{useState,useContext,useEffect,useRef} from "react";
import Input from "../../../globalComponents/Input";
import ReactSlider from 'react-slider';
import fetcher from "../../../functions/fetcher";
import compare from "../../../functions/compare";
import formater from "../../../functions/formater";
import CheckBox from "../../../globalComponents/CheckBox";
import {SearchContext} from "../../../App";
const Article=()=>{
	const [bookName,setBookName]=useState("");
	const [maxResults,setMaxResults]=useState(40);
	const [bookType,setBookType]=useState("all");
	const [useStrictMode,setUseStrictMode]=useState(false);
	const [author,setAuthor]=useState("");
	const [category,setCategory]=useState("");
	const [publisher,setPublisher]=useState("");
	const {searchActive,setSearchActive}=useContext(SearchContext);
	const requestTimeout=useRef();
	const maxResultsChangeHandler=(data)=>{
		setMaxResults(data);
		new Request();
	}
	const bookTypeChangeHandler=(data)=>{
		setBookType(data);
		new Request();
	}
	const useStrictModeChangeHandler=(data)=>{
		setUseStrictMode(data);
		new Request();
	}
	class Request {
		foundItems=[];
		constructor() {
				clearTimeout(requestTimeout.current);
				requestTimeout.current=setTimeout(()=>{
					console.log("request");
					if (!searchActive) setSearchActive(true);
					if (useStrictMode) {}
					else this.massiveRequest();
				},1500)
		}
		massiveRequest() {
			let mass=[
				{data:author,type:"inauthor"},
				{data:category,type:"subject"},
				{data:publisher,type:"inpublisher"}
			].forEach((elem)=>{
				if (elem.data=="") return;
				this.fastRequest(elem);
			})
		}
		async fastRequest(options) {
			try {
				const response=await fetcher(bookName,options,bookType,maxResults);
				let {items}=await response.json();
				items=items.map(elem=>formater(elem));
				items=items.filter(elem=>{
					for (let i=0;i<this.foundItems.length;i++) {
						if (compare(this.foundItems[i],elem)) return false;
					}
					return true;
				});
				this.foundItems.push(...items);
			}
			catch (error) {
				console.log(error);
			}
		}
	}
	return (<article className="main__article beet2">
		<p className="main__title blackTitle">Search for books</p>
		<Input placeholder="type the name of the book" style="main__mainInput" setData={setBookName}/>
		<div className="width">
			<p className="main__text main-margin">Max Number</p>
			<ReactSlider
				defaultValue={maxResults}
				min={1}
				max={40}
				onChange={maxResultsChangeHandler}
				renderThumb={(props)=><div className="thumb-0" {...props}>{maxResults}</div>}
			/>
		</div>
		<CheckBox text="use strict mode" bool={useStrictMode} setBool={useStrictModeChangeHandler}/>
		<div className="main__radioButtons">
			<Radio type="all" setData={bookTypeChangeHandler} data={bookType}/>
			<Radio type="ebooks" setData={bookTypeChangeHandler} data={bookType}/>
			<Radio type="free-ebooks" setData={bookTypeChangeHandler} data={bookType}/>
			<Radio type="paid-ebooks" setData={bookTypeChangeHandler} data={bookType}/>
		</div>
		<div className="main__container">
			<p>Author</p>
			<Input placeholder="J K Rowling" setData={setAuthor}/>
		</div>
		<div className="main__container">
			<p>Category</p>
			<Input placeholder="Fantasy" setData={setCategory}/>
		</div>
		<div className="main__container">
			<p>Publisher</p>
			<Input placeholder="RELX" setData={setPublisher}/>
		</div>
	</article>)
}
export default Article;
const Radio=({type,setData,data})=>{
	const clickHandler=()=>{
		if (type==data) return;
		setData(type);
	}
	return (<div className="beet stretch">
		<div className="main__radio center" onClick={clickHandler}>
			{data==type && <div/>}
		</div>
		<p className="main__text main-left">{type}</p>
	</div>
	)
}
