import React,{useState,useContext,useEffect} from "react";
import fetcher from "../../../functions/fetcher";
import formater from "../../../functions/formater";
import topics from "../topics";
import BookCard from "../../../globalComponents/BookCard";
import compare from "../../../functions/compare";
const OfferBooks=()=>{
	const [data,setData]=useState([]);
	const random=(a,b)=> {
		return Math.floor(a+Math.random()*(b+1-a));
	}
	useEffect(()=>{
		const requestNumber=6;
		const previousTopics=JSON.parse(localStorage.getItem("previousTopics"));
		const randomTopics=[...topics];
		randomTopics.sort(()=>0.5-Math.random());
		let mass=[
			{title:"Books from author you liked",key:"likedBookAuthors"}
			,{title:"Books you were searching for",key:"prevBookTopics"},
			{title:"Books from categories you liked",key:"prevLikedBooks"}
		].forEach(async ({title,key})=>{
			if (previousData[key].length==0) return;
			requestNumber--;
			let requestData;
			if (random(0,1)==0) {
				previousData[key].sort(()=>0.5-Math.random());
				requestData=previousData[key][0];
			} else {
				requestData=previousData[key][previousData[key].length-1];
			}
			request(requestData,title)
		})
		for (let i=0;i<requestNumber;i++) request(randomTopics[i],randomTopics[i])
	},[]);
	const sort=(items)=>{
		if (random(0,1)==0) {
			items.sort(()=>0.5-Math.random());
		} else {
			const rightAnswer="/static/media/starFilled.2ebd54fe5113a74ba3cd.png";
			const count=({ratingStarsArray})=>{
				let result=0;
				ratingStarsArray.forEach((elem)=>{
					if (elem==rightAnswer) result++;
				});
				return result;
			}
			items.sort((next,curr)=>{
				return count(curr)-count(next);
			})
		}
	}
	const filter=(items)=>{
		let mass=JSON.parse(localStorage.getItem("favoriteBooks"));
		return items.filter((elem)=>{
			for (let i=0;i<mass.length;i++) {
				if (compare(mass[i],elem)) return false;
			}
			return true;
		})
	}
	const request=async (mass,title)=> {
		try {
			let response=await fetcher(mass);
			let {items}=await response.json();
			items=items.map((elem)=>formater(elem));
			items=filter(items);
			sort(items);
			setData((curr)=>[...curr,{title:title,elements:items.slice(0,6)}]);
		}
		catch (error) {
			console.log(error);
		}
	}
	return (
		data.map(({title,elements})=><>
			<p className="title">{title}</p>
			<p className="main__line"/>
			{elements.map((elem)=><BookCard data={elem} mode="normal"/>)}
		</>)
	);
};
export default OfferBooks;
