import React,{useState,useContext,useEffect} from "react";
import "../../assets/styles/main.css";
import topics from "./topics";
import BookCard from "../../globalComponents/BookCard"
import {SearchContext} from "../../App";
import fetcher from "../../functions/fetcher"
const CreateMainScreen = () => {
	const {searchActive}=useContext(SearchContext);
  return (
    <div className="main beet">
			<Article/>
      <article className="main__article" ></article>
      <div className="main__body">
			{searchActive ? <SearchResults/> : <OfferBooks/>}
			</div>
    </div>
  );
};
export default CreateMainScreen;
const SearchResults=()=>null;
const Article=()=>null;
const OfferBooks=()=>{
	const [data,setData]=useState([]);
	useEffect(async ()=>{
		const requestNumber=6;
		const previous=JSON.parse(localStorage.getItem("searchInfo"));
		const randomTopics=[...topics];
		randomTopics.sort(()=>0.5-Math.random());
		if (previous)
		for (let i=0;i<requestNumber;i++) {
			try {
				let response=await fetcher(randomTopics[i]);
				let obj=await response.json();
				obj.sort(()=>0.5-Math.random());
				setData([...data,{title:randomTopics[i],elements:obj.slice(0,6)}])
			}
			catch (error) {
				console.log(error);
			}
		}

	},[])
	return (
		data.map(({title,elements})=><>
			<p className="title">{title}</p>
			<p className="main__line"></p>
			{elements.map((elem)=><BookCard {...elem}/>)}
		</>)
	);
};
