import React from "react";
import Error from "../assets/images/error.png";
export const GenerateMistake=({text})=>{
	return (<div className="width beet">
		<img src={Error} className="screen__errorImage"/>
		<p className="screen__error">{text}</p>
	</div>)
}
export const conditions={
	password:(data)=>{
		return !(data.length>7 && data.toLocaleLowerCase()!=data && data.toUpperCase()!=data)
	}
}
