import { useEffect, useMemo,useState } from "react";
const App=()=> {
  const [state,setState]=useState([]);
  const lists=useMemo(()=>{
    let mass=state.filter((elem)=>{
      return elem.volumeInfo.imageLinks!=undefined
    })

    return mass.map((elem)=>{
      const link=elem.volumeInfo.imageLinks;
      return <Image image={link.thumbnail} thumbnail={link.smallThumbnail}/>
    })
  },[state.length])
  useEffect(() => {
    const key = "AIzaSyBEIqK7j9rLo7FHM4Fdl41EITEsGLHFx9E", book = "Harry Poter";
    fetch(`https://www.googleapis.com/books/v1/volumes/?q=${book}&key=${key}&maxResults=40`)
    .then((res) => {
      res.json().then((mess)=>{
        setState(mess.items)
      })
    });
  }, []);
  return (<>
    {lists}
  </>)
}
export default App;
const Image=({image,thumbnail})=>{
  const [state,setState]=useState(image)
  return <img src={state} onMouseEnter={()=>setState(thumbnail)} onMouseLeave={()=>setState(image)}/>
}
