import React, { useState, useContext, useEffect, useRef,createContext, useMemo } from "react";
import EmptyLibrary from "../../assets/images/emptyLibrary.png";
import Input from "../../globalComponents/Input";
import Plus from "../../assets/images/plus.png";
const CreateLibraryScreen=()=>{
	const [libraries,setLibraries]=useState(JSON.parse(localStorage.getItem("libraries")) || []);
	return (<div className="library screen">
		<div className="library__form">
			<div className="beet">
				<Input placeholder="type the name of the library"/>
				<div className="library__button button blue">
					<div className="beet">
						<img src={Plus}/>
						<p>Create library</p>
					</div>
				</div>
			</div>
		</div>
		{libraries.length==0 ? <div className="popup beet2 stretch">
			<img src={EmptyLibrary}/>
			<p>There is bookshelfs in your library , click to create one</p>
		</div> : null}
	</div>)
}
export default CreateLibraryScreen;
