import Ok from "../assets/images/ok.png";
const CheckBox=({text,bool,setBool})=>{
	return (<div className="beet stretch">
		<div className="checkBox center" onClick={()=>setBool(curr=>!curr)}>
			{bool && <img src={Ok}/>}
		</div>
		<p className="left">{text}</p>
	</div>)
}
export default CheckBox;
