import starFilled from "../assets/images/starFilled.png";
import starHalfFilled from "../assets/images/starHalfFilled.png";
import starEmpty from "../assets/images/starEmpty.png"
export default (obj)=>{
	const {accessInfo,volumeInfo,saleInfo}=obj;
	const canBeBought=saleInfo.saleability=="FOR_SALE";
	const prices=canBeBought ? {
		listPrice:saleInfo.listPrice.amount + " "+ saleInfo.listPrice.currencyCode,
		retailPrice:saleInfo.retailPrice.amount+saleInfo.retailPrice.currencyCode
	} : {};
	function makeString(mass) {
		return mass ?	mass.reduce((curr,next)=>curr+" "+next) : "Unknown"
	}
	function getStars(amount) {
		const mass=[] , roundNumber=Math.floor(amount);
		for (let i=0;i<roundNumber;i++) {
			mass.push(starFilled)
		}
		if (amount!=roundNumber) mass.push(starHalfFilled);
		for (let i=0;i<5-mass.length;i++) {
			mass.push(starEmpty)
		}
		return mass;
	}
	const result={
		title:volumeInfo.title,
		image:volumeInfo.imageLinks.thumbnail,
		epub:accessInfo.epub.isAvailable,
		pdf:accessInfo.pdf.isAvailable,
		type:saleInfo.isEbook ? "Electronic" : "Non electronic",
		canBeBought:canBeBought,
		...prices,
		country:saleInfo.country,
		authors:makeString(volumeInfo.authors),
		ratingStarsArray:getStars(volumeInfo.averageRating),
		categories:makeString(volumeInfo.categories),
		description:volumeInfo.description,
		language:volumeInfo.language,
		pageCount:volumeInfo.pageCount,
		subtitle:volumeInfo.subtitle,
		publishedDate:volumeInfo.publishedDate,
		link:canBeBought ? saleInfo.buyLink : volumeInfo.previewLink
	}
	console.log(result);
	return result;
}
