export default (book,options={data:"",type:""},filters,results=40)=>{
	const key = "AIzaSyAKv90XvsFYWOgmGgTvGdPfqNiQYRWp-Y8",
	url="https://www.googleapis.com/books";
	const option=(options.type!="") ? `+${options.type}:${options.data}` : "";
	const filter=(filters!=undefined && filters!="all") ? `&filter=${filters}` : ``;
  return fetch(`${url}/v1/volumes/?q=${book}${option}${filter}&key=${key}&maxResults=${results}`);
}
