import Search from "../assets/images/search.png";
import Cross from "..//assets/images/cross.png";
import React,{useRef,useState,useContext,useEffect} from "react";
const Input=({setData,placeholder,style=""})=>{
	const [active,setActive]=useState(false);
	const input=useRef();
	const changeHandler=(eve)=>{
		const value=eve.target.value;
		if (value=="") setActive(false);
		else {
			if (!active) setActive(true);
			setData(value);
		}
	}
	const clearHandler=()=>{
		input.current.value="";
		setActive(false);
		setData("");
	}
	return (<div className={"input "+style}>
		{active && <img src={Search} className="input__icon"/>}
		<input placeholder={placeholder} onChange={changeHandler} ref={input}/>
		{!active && <img src={Cross} className="input__clear" onClick={clearHandler}/>}
	</div>);
}
export default Input;
