import React, { useState, useContext, useEffect, useRef } from "react";
import Input from "../../../globalComponents/Input";
import ReactSlider from "react-slider";
import fetcher from "../../../functions/fetcher";
import compare from "../../../functions/compare";
import formater from "../../../functions/formater";
import random from "../../../functions/random";
import { SearchContext } from "../../../App";
import BookModeSwitcher from "../../../globalComponents/BookModeSwitcher";
import Sorter from "./Sorter";
import Radio from "./Radio";
const Article = ({setFoundItems, foundItems,setBookView,bookView }) => {
  const [bookName, setBookName] = useState("");
  const [maxResults, setMaxResults] = useState(300);
  const [bookType, setBookType] = useState("all");
  const [useStrictMode, setUseStrictMode] = useState(false);
  const [author, setAuthor] = useState("");
  const [category, setCategory] = useState("");
  const [publisher, setPublisher] = useState("");
  const { searchActive, setSearchActive } = useContext(SearchContext);
  const requestTimeout = useRef();
  class Request {
		options=[
			{ data: author, type: "inauthor" },
      { data: category, type: "subject" },
      { data: publisher, type: "inpublisher" }
		]
    constructor() {
      clearTimeout(requestTimeout.current);
      if (bookName == "") return;
      requestTimeout.current = setTimeout(() => {
        foundItems=[];
        setFoundItems([]);
        if (!searchActive) setSearchActive(true);
				if (useStrictMode) this.accurateRequest();
				else this.massiveRequest();
      }, 1500);
    }
    async massiveRequest() {
			let results=[];
      try {
        const response = await fetcher(
          bookName,
          undefined,
          bookType,
          maxResults
        );
				let { items } = await response.json();
				results=this.format(items);
      } catch (error) {
        console.error(error);
      }
      setFoundItems((curr) => [...curr, ...results]);
      this.options.forEach((elem) => {
        if (elem.data == "") return;
        this.fastRequest(elem);
      });
    }
    async fastRequest(options) {
      let results = [];
      try {
        const response = await fetcher(bookName, options, bookType, maxResults);
				let {items} = await response.json();
        results = this.format(items);
      } catch (error) {
        console.error(error);
      } finally {
        results = results.filter((elem) => {
          for (let i = 0; i < foundItems.length; i++) {
						if (compare(foundItems[i], elem)){
							return false;
						}
          }
          return true;
        });
				setFoundItems((curr) => [...curr, ...results]);
      }
		}
		async accurateRequest() {
			let results=[];
			try {
				const response=await fetcher(bookName,this.getOptions(),bookType,maxResults);
				let {items}=await response.json();
				results=this.format(items);
			} catch (error) {
				console.error(error)
			} finally {
				setFoundItems([...results])
			}
		}
		getOptions() {
			const mass=this.options.filter(({data},ind)=>{
				return data!="";
			});
			if (mass.length==0) return undefined;
			else if (mass.length==1) return mass[0];
			else return mass[random(0,mass.length-1)];
		}
		format(items=[]) {
			return items.map(elem=>formater(elem));
		}
  }
	const data={
    bookName,
    bookType,
    maxResults,
    useStrictMode,
    author,
    category,
    publisher
  }
  useEffect(() => {
    new Request();
  },[...Object.values(data)]);
  return (
    <article className="main__article beet2">
      <p className="main__title blackTitle">Search for books</p>
      <Input
        placeholder="type the name of the book"
        style="main__mainInput"
        setData={setBookName}
      />
      <div className="width">
        <p className="main__text main-margin">Max Number</p>
        <ReactSlider
          defaultValue={maxResults}
          min={1}
          max={600}
          onChange={(data) => setMaxResults(data)}
          renderThumb={(props) => (
            <div className="thumb-0" {...props}>
              {maxResults}
            </div>
          )}
        />
      </div>
      <div className="main__radioButtons">
        <Radio type="all" setData={setBookType} data={bookType} />
        <Radio type="ebooks" setData={setBookType} data={bookType} />
        <Radio type="free-ebooks" setData={setBookType} data={bookType} />
        <Radio type="paid-ebooks" setData={setBookType} data={bookType} />
      </div>
      <div className="main__container">
        <p>Author</p>
        <Input placeholder="J K Rowling" setData={setAuthor} />
      </div>
      <div className="main__container">
        <p>Category</p>
        <Input placeholder="Fantasy" setData={setCategory} />
      </div>
      <div className="main__container">
        <p>Publisher</p>
        <Input placeholder="RELX" setData={setPublisher} />
      </div>
      {searchActive && <div className="width beet2">
        <p className="main__title blackTitle">Manage Selected</p>
        <BookModeSwitcher setData={setBookView} data={bookView}/>
        <div className="width beet"></div>
        <div className="main__sorters beet">
          <Sorter type="money"/>
          <Sorter type="rating"/>
          <Sorter type="date"/>
        </div>
      </div>}
    </article>
  );
};
export default Article;
