import React, { useState, useEffect, useRef, useMemo } from "react";
import Logo from "../assets/images/logo.png";
import YouTube from "../assets/images/youTube.png";
import Facebook from "../assets/images/facebook.png";
import Instagram from "../assets/images/instagram.png";
import {StyleRoot} from 'radium';
import animations from "../animations";
const Header = () => {
  const [mobileMode, setMobileMode] = useState(false);
  const [isMobileMenuOpened,setIsMobileMenuOpened]=useState(false);
  const resize = () => {
    const width = window.innerWidth;
    if (width > 900 && mobileMode) {
      setMobileMode(false);
      setIsMobileMenuOpened(false);
    }
    else if (width < 900 && !mobileMode) setMobileMode(true);
  };
  useEffect(() => {
    window.addEventListener("resize", resize);
    return () => window.removeEventListener("resize", resize);
  });
  useEffect(() => resize(), []);
  return (<>
    <div className="header beet">
      <img className="header__logo" src={Logo} />
      {!mobileMode && <HeaderContent/>}
      {mobileMode && <Burger setIsMobileMenuOpened={setIsMobileMenuOpened}/>}
    </div>
    {isMobileMenuOpened && <Popup/>}
  </>);
};
export default Header;
const Burger = ({setIsMobileMenuOpened}) => (
  <div className="header__burger beet2" onClick={()=>setIsMobileMenuOpened(curr=>!curr)}>
    <p></p>
    <p></p>
    <p></p>
    <p></p>
  </div>
);
const HeaderContent = () => (
  <>
  <div className="header__titles beet">
    <p>Password generator</p>
    <p>Library</p>
    <p>Sign In</p>
    <p>Sign up</p>
  </div>
  <div className="header__media beet">
    <div className="header__ball center">
      <img src={YouTube} />
    </div>
    <div className="header__ball center">
      <img src={Facebook} className="header__imgHeight" />
    </div>
    <div className="header__ball center">
      <img src={Instagram} />
    </div>
  </div>
  </>
);
const Popup=()=>(
  <StyleRoot style={animations.slideInDown}>
    <HeaderContent/>
  </StyleRoot>
)
