import React, { useCallback, useEffect, useState, useMemo,useContext } from "react";
import {StyleRoot} from 'radium';
import animations from "../animations";
import NotSelected from "../assets/images/favourite.png";
import Selected from "../assets/images/favouriteActive.png";
import compare from "../functions/compare";
import Eye from "../assets/images/eye.png";
import starFilled from "../assets/images/starFilled.png";
import starHalfFilled from "../assets/images/starHalfFilled.png";
import starEmpty from "../assets/images/starEmpty.png";
import { useNavigate } from "react-router-dom";
const Book = ({ data, mode }) => {
  //!!bag fix
  const isInFavorites = useMemo(() => {
    const favoriteBooks = JSON.parse(localStorage.getItem("favoriteBooks"));
    for (let i = 0; i < favoriteBooks.length; i++) {
      if (compare(favoriteBooks[i], data)) return true;
    }
    return false;
  });
  const [liked, setLiked] = useState(false);
  const getStars = (amount) => {
    const mass = [],
      roundNumber = Math.floor(amount);
    for (let i = 0; i < roundNumber; i++) {
      mass.push(starFilled);
    }
    if (amount != roundNumber) mass.push(starHalfFilled);
    const length = mass.length;
    for (let i = 0; i < 5 - length; i++) {
      mass.push(starEmpty);
    }
    return mass.map((elem) => <img src={elem} />);
  };
  const returnBook = () => {
    const title = <p className="book__title">{data.title}</p>,
      stars = <div className="book__stars">{getStars(data.rating)}</div>;
    if (mode == "normal")
      return (
        <StyleRoot className="book beet2 book__align book-max" style={animations.zoomIn}>
          <div className="book__imageBlock">
            <ShowPreview data={data} />
            <Image image={data.image} style="book__imageBig" />
            <Hearth {...{ liked, setLiked, data }} style="book-eyeLeft"/>
          </div>
          {title}
          <div className="book__container beet">
            {stars}
            <Authors authors={data.authors} />
          </div>
          <Button data={data} style="book__normalButton" />
        </StyleRoot>
      );
    else if (mode == "large")
      return (
        <StyleRoot className="book beet" style={animations.zoomIn}>
          <div className="book__largeBlock">
            <ShowPreview data={data} />
            <Image image={data.image} style="book__imageSmall" />
            <Hearth {...{ liked, setLiked, data }} />
          </div>
          <div className="book__largeContainer">
            {title}
            <div className="book__mass beet">
              <div className="beet">
                {stars}
                <Authors authors={data.authors} />
              </div>
              <Button style="book__largeButton" data={data} />
            </div>
            <p className="book__description">{data.description}</p>
          </div>
        </StyleRoot>
      );
    else
      return (
        <div className="book beet2">
          <Image image={data.image} style="book__imageSmall" />
          {title}
        </div>
      );
  };
  useEffect(()=>{
    if (liked!=isInFavorites) {
      setLiked(isInFavorites);
    }
  },[isInFavorites]);
  return returnBook();
};
export default Book;
const Button = ({ data, style }) => (
  <a className={"button " + style} href={data.link} target="_blank">
    <p>
      {data.canBeBought ? `Buy` : "Read"} for{" "}
      <span>{data.canBeBought ? data.listPrice : "free"}</span>
    </p>
  </a>
);
const Hearth = ({ liked, setLiked, data }) => {
  const clickHandler = () => {
    const favoriteBooks = JSON.parse(localStorage.getItem("favoriteBooks"));
    if (!liked) favoriteBooks.push(data);
    else {
      let index;
      favoriteBooks.forEach((elem, ind) => {
        if (compare(elem, data)) index = ind;
      });
      favoriteBooks.splice(index, 1);
    }
    setLiked((curr) => !curr);
    localStorage.setItem("favoriteBooks", JSON.stringify(favoriteBooks));
  };
  return (
    <img
      className="book__hearth"
      src={liked ? Selected : NotSelected}
      onClick={clickHandler}
    />
  );
};
const Authors = ({ authors }) => {
  const [show, setShow] = useState(false);
  const leaveHandler = () => {
    setTimeout(() => {
      setShow(false);
    }, 2000);
  };
  return (
    <div className="book__relative">
      {show && <p className="book__hiddenAuthors">{authors}</p>}
      <p
        className="book__author"
        onMouseEnter={() => setShow(true)}
        onMouseLeave={leaveHandler}
      >
        {authors}
      </p>
    </div>
  );
};
const Image = ({ image, style }) => <img src={image} className={style} />;
const ShowPreview = ({ data,style }) => {
  const navigate = useNavigate();
  const clickHandler = () => {
    navigate("/preview", { state: data });
  };
  return <img src={Eye} className={"book__eye "+style} onClick={clickHandler} />;
};
